›†çš„å›¾åƒæ–‡ä»¶ï¼‰ï¼š prepare.py12345678910111213141516171819202122232425262728293031323334353637383940414243444546474849505152535455565758596061626364656667686970717273747576777879808182838485868788899091929394import osimport jsonimport shutil# Save pathstrain_path = 'datasets/collection/train'valid_path = 'datasets/collection/valid'# Create directories if not existif not os.path.exists(train_path): os.makedirs(train_path)if not os.path.exists(valid_path): os.makedirs(valid_path)def Add_Class_COCO_Data(dataset_path, classname, dataset, dataset_info): with open(os.path.join(dataset_path, '_annotations.coco.json'), 'r') as file: class_data = json.load(file) # check if the class already exists in the train data class_exist, class_id = False, None for item in dataset['categories']: if item['name'] == classname: class_exist, class_id = True, item['id'] if class_exist is False: class_id = dataset_info['category_id'] dataset['categories'].append({'id': class_id, 'name': classname}) dataset_info['category_id'] += 1 # add the class images for the train data image_id = dataset_info['img_id'] for image in class_data['images']: image_info = {'id': dataset_info['img_id'], 'file_name': image['file_name'], 'width': image['width'], 'height': image['height']} dataset['images'].append(image_info) dataset_info['img_id'] += 1 # add the class annotations for the train data for ann in class_data['annotations']: ann_info = {'id': dataset_info['ann_id'], 'image_id': image_id+ann['image_id'], 'category_id': class_id, 'bbox': ann['bbox'], 'area': ann['area'], 'iscrowd': ann['iscrowd']} dataset['annotations'].append(ann_info) dataset_info['ann_id'] += 1def Copy_Files(src_dir, dst_dir, skip_files=['_annotations.coco.json']): &quot;&quot;&quot; Copy the files from source directory to the destination directory. &quot;&quot;&quot; os.makedirs(dst_dir, exist_ok=True) for file_name in os.listdir(src_dir): # skip some files if file_name in skip_files: continue src_file = os.path.join(src_dir, file_name) dst_file = os.path.join(dst_dir, file_name) if os.path.isfile(src_file): shutil.copy(src_file, dst_file) print(f&quot;Copied: {src_file} -&gt; {dst_file}&quot;) else: print(f&quot;Skipping directory: {src_file}&quot;)# training classesclasses = [&quot;face&quot;, &quot;phone&quot;]# store the combined datacategory_id = 0train_data = {'categories': [], 'images': [], 'annotations': []}train_info = {'category_id': 0, 'img_id': 0, 'ann_id': 0}valid_data = {'categories': [], 'images': [], 'annotations': []}valid_info = {'category_id': 0, 'img_id': 0, 'ann_id': 0}class_data_root = './data/collection'for cls_name in classes: class_train_path = os.path.join(class_data_root, cls_name, 'train') class_valid_path = os.path.join(class_data_root, cls_name, 'valid') Add_Class_COCO_Data(class_train_path, cls_name, train_data, train_info) Add_Class_COCO_Data(class_valid_path, cls_name, valid_data, valid_info) Copy_Files(class_train_path, train_path) Copy_Files(class_valid_path, valid_path)# Save data to filewith open(os.path.join(train_path, '_annotations.coco.json'), 'w') as f: json.dump(train_data, f, indent=4)with open(os.path.join(valid_path, '_annotations.coco.json'), 'w') as f: json.dump(valid_data, f, indent=4)print(f&quot;&gt;&gt;&gt; length of categories: {len(train_data['categories'])}&quot;)print(f&quot;&gt;&gt;&gt; length of train_data: {len(train_data['images'])}&quot;)print(f&quot;&gt;&gt;&gt; length of valid_data: {len(valid_data['images'])}&quot;) åˆå¹¶åçš„ç»Ÿä¸€çš„è®­ç»ƒå’ŒéªŒè¯æ•°æ®é›†å…¨éƒ¨æ”¾åœ¨äº† datasets/collection ç›®å½•ä¸‹é¢ï¼Œä»¥ä¾¿äºåç»­æ¨¡å‹è®­ç»ƒä½¿ç”¨ã€‚ ä¸‹è½½é¢„è®­ç»ƒæ¨¡å‹å‚è€ƒ Face Detection - Swift-YOLO ä¸‹è½½é¢„è®­ç»ƒæ¨¡å‹æƒé‡æ–‡ä»¶ pretrain.pthï¼Œç„¶åä¿å­˜åœ¨ ModelAssistant/checkpoints æ–‡ä»¶å¤¹ä¸‹ã€‚ è®­ç»ƒ YOLO æ¨¡å‹åœ¨ ModelAssistant é¡¹ç›®ä¸‹ï¼Œé‡‡ç”¨çš„æ˜¯ä»“åº“æä¾›çš„ Swift YOLO æ¨¡å‹ï¼Œä½œè€…è§£é‡Šè¯´æ˜è¯¥æ¨¡å‹å…·æœ‰ä¼˜åŒ–çš„ç«¯ä¾§è¿è¡Œæ€§èƒ½ï¼šâ€We implemented a lightweight object detection algorithm called Swift YOLO, which is desi