(self, dec_voc_size, max_len, n_layers, n_head, d_model, d_ff, dropout): super(Decoder, self).__init__() decoder_layer = DecoderLayer(n_head, d_model, d_ff, dropout) self.layers = clones(decoder_layer, n_layers) self.emb = Embedding(vocab_size=dec_voc_size, d_model=d_model, max_len=max_len, dropout=dropout) self.norm = LayerNorm(decoder_layer.d_model) def forward(self, tgt, enc_src, src_mask, tgt_mask): tgt = self.emb(tgt) # embedded the input_ids for layer in self.layers: tgt = layer(tgt, enc_src, src_mask, tgt_mask) return self.norm(tgt) Transformeråœ¨å®Œæˆä¸Šè¿°å„æ¨¡å—çš„è®¾è®¡åï¼Œå¯ä»¥å¾—åˆ°å®Œæ•´çš„ Transformer æ¨¡å‹ï¼Œå…¶ç»“æ„å¦‚ä¸‹ï¼š Transformer1234567891011121314151617181920212223242526272829303132333435363738394041424344454647484950515253545556575859606162636465666768697071727374757677&quot;&quot;&quot;@author: Zhihao Li@date: 2024-11-11@homepage: https://zhihaoli.top/&quot;&quot;&quot;import torchimport torch.nn as nnfrom torch.nn.functional import log_softmaxfrom model.encoder import Encoderfrom model.decoder import Decoderclass Generator(nn.Module): &quot;Define standard linear + softmax generation step.&quot; def __init__(self, d_model, vocab): super(Generator, self).__init__() self.proj = nn.Linear(d_model, vocab) def forward(self, x): return log_softmax(self.proj(x), dim=-1)class Transformer(nn.Module): &quot;&quot;&quot; A standard Transformer architecture. Base for this and many other models. &quot;&quot;&quot; def __init__(self, src_pad_idx, tgt_pad_idx, tgt_bos_idx, enc_voc_size, dec_voc_size, d_model, n_head, max_len, d_ff, n_layers, dropout, device): super().__init__() self.src_pad_idx = src_pad_idx self.tgt_pad_idx = tgt_pad_idx self.tgt_bos_idx = tgt_bos_idx self.device = device self.encoder = Encoder(enc_voc_size=enc_voc_size, max_len=max_len, n_layers=n_layers, n_head=n_head, d_model=d_model, d_ff=d_ff, dropout=dropout) self.decoder = Decoder(dec_voc_size=dec_voc_size, max_len=max_len, n_layers=n_layers, n_head=n_head, d_model=d_model, d_ff=d_ff, dropout=dropout) self.generator = Generator(d_model, dec_voc_size) def forward(self, src, tgt): &quot;Take in and process masked src and target sequences.&quot; src_mask = self.make_src_mask(src) tgt_mask = self.make_tgt_mask(tgt) enc_src = self.encoder(src, src_mask) dec_tgt = self.decoder(tgt, enc_src, src_mask, tgt_mask) return self.generator(dec_tgt) def make_src_mask(self, src): &quot;&quot;&quot; Mask the padding tokens int source sentence. &quot;&quot;&quot; src_mask = (src != self.src_pad_idx).unsqueeze(1).unsqueeze(2) return src_mask def make_tgt_mask(self, tgt): &quot;&quot;&quot; Mask the padding tokens int target sentence. &quot;&quot;&quot; tgt_pad_mask = (tgt != self.tgt_pad_idx).unsqueeze(1).unsqueeze(3) tgt_len = tgt.shape[1] tgt_sub_mask = torch.tril(torch.ones(tgt_len, tgt_len)).type(torch.ByteTensor).to(self.device) tgt_mask = tgt_pad_mask &amp; tgt_sub_mask return tgt_mask Exploration From ScratchPreparationClone Projectå‡†å¤‡æ¢ç´¢ä¹‹å‰ï¼Œéœ€è¦å°† TransformerPractice é¡¹ç›®å…‹éš†ä¸‹æ¥ï¼Œå¯ä»¥ä½¿ç”¨å¦‚ä¸‹å‘½ä»¤å…‹éš†åˆ°æœ¬åœ°ï¼š 1git clone https://github.com/LZHMS/TransformerPractice.git é¡¹ç›®ä¸­å·²ç»é›†æˆå¥½äº†æ‰€æœ‰å¿…è¦çš„æ¨¡å‹ç»„ä»¶å¹¶é€šè¿‡ä¸åŒçš„ Trainers ä¸²è”èµ·æ¥ï¼Œä»¥å®Œæˆç‰¹å®šçš„ä¸‹æ¸¸ä»»åŠ¡ã€‚ Install Conda Environmentå®‰è£… conda ç¯å¢ƒï¼Œtokenizer ä½¿ç”¨æœ€æ–°çš„ spacy åº“ï¼Œå…¶ä»–åº“çš„ç‰ˆæœ¬ä¹Ÿéƒ½æ˜¯å…¼å®¹ä¸‹æ¯”è¾ƒæ–°çš„ï¼Œå¯ä»¥é€šè¿‡ä»¥ä¸‹å‘½ä»¤è¿›è¡Œç¯å¢ƒé…ç½®ï¼š 1conda env create -f environment.yml Download the Datasetæœ¬é¡¹ç›®ä½¿ç”¨ Multi30K Dataset æ•°æ®é›†è®­ç»ƒå’Œè¯„ä¼°æ–‡æœ¬ç¿»è¯‘æ¨¡å‹ï¼Œå…·ä½“éœ€è¦å…ˆåœ¨å®˜ç½‘ä¸Šä¸‹è½½æ•°æ®é›†ç„¶åæå– task1 çš„æ‰€æœ‰æ–‡ä»¶ï¼Œå°†å…¶æ”¾ç½®åœ¨ç›®å½• data/multi30k ä¸‹ã€‚è¯¦ç»†ç›®å½•ç»“æ„å¯ä»¥è§ä¸‹æ–‡ï¼š Category Structure12345678910.â”œâ”€ dataâ”‚ â”œâ”€ multi30kâ”‚ â”‚ â”œâ”€ task1â”‚ â”‚ â”‚ â”œâ”€ ...â”œâ”€ datasetâ”œâ”€ model â”œâ”€ outputâ”œâ”€ trainerâ””â”€ model Explore the Moduleså¯¹äº Transformer å¤„ç†æµç¨‹çš„æ¢ç´¢ï¼Œå¯ä»¥åœ¨ Jupyter Notebook ä¸­å•æ­¥æ¼”ç¤ºã€‚ ä¸ºäº†æ›´å¥½åœ°ä½